import React, { useState } from 'react';
import { addBin } from '../../utils/BinsUtils';
import Button from '../button/Button';
import classes from './BinForm.module.css';

const BinForm = ({ onAddBin }) => {
  const [address, setAddress] = useState('');
  const [city, setCity] = useState('');
  const [type, setType] = useState('');

  const handleAddBin = async () => {
    const currentDate = new Date().toISOString(); // Get the current date

    try {
      const newBin = {
        address,
        city,
        type,
        last_modified: currentDate,
      };

      await addBin(newBin);
      onAddBin(newBin);
      console.log('Bin added successfully!');

      // Reset input fields
      setAddress('');
      setCity('');
      setType('');
    } catch (error) {
      console.error('Error adding bin:', error);
    }
  };

  return (
    <div className={classes.container}>
      <input
        type="text"
        placeholder="Address"
        value={address}
        onChange={(e) => setAddress(e.target.value)}
        required
      />
      <input
        type="text"
        placeholder="City"
        value={city}
        onChange={(e) => setCity(e.target.value)}
        required
      />
      <input
        type="text"
        placeholder="Type"
        value={type}
        onChange={(e) => setType(e.target.value)}
        required
      />
      <Button name="Add Bin" fun={handleAddBin} />
    </div>
  );
};

export default BinForm;
