// pages/LoginRegister/LoginRegister.js

import React, { useContext, useState } from 'react';
import { AuthContext } from '../../context/authContext';
import Form from '../../components/Form/Form';
import styles from './LoginRegister.module.css';

function LoginRegister() {
  const { login, registered } = useContext(AuthContext);

  const [err, setError] = useState(null);
  const [showRegisterForm, setShowRegisterForm] = useState(false);

  const handleLoginSubmit = async (formData) => {
    try {
      await login(formData);
    } catch (err) {
      setError(err.response.data);
    }
  };

  const handleRegisterSubmit = async (formData) => {
    setError(null);
    try {
      await registered(formData);
      // Handle registration completion logic if needed
    } catch (err) {
      setError(err.response.data);
    }
  };

  // Fields for the login form
  const loginFields = [
    {
      name: 'username',
      className: styles.dataInputs,
      type: 'text',
      placeholder: 'User Name',
      required: true,
    },
    {
      name: 'password',
      className: styles.dataInputs,
      type: 'password',
      placeholder: 'Password',
      required: true,
    },
  ];

  // Fields for the registration form
  const registerFields = [
    {
      name: 'username',
      className: styles.dataInputs,
      type: 'text',
      placeholder: 'User Name',
      required: true,
    },
    {
      name: 'email',
      className: styles.dataInputs,
      type: 'email',
      placeholder: 'Email',
      required: true,
    },
    {
      name: 'password',
      className: styles.dataInputs,
      type: 'password',
      placeholder: 'Password',
      required: true,
    },
    {
      name: 'confirm_password',
      className: styles.dataInputs,
      type: 'password',
      placeholder: 'Confirm Password',
      required: true,
    },
    {
      name: 'city',
      className: styles.dataInputs,
      type: 'text',
      placeholder: 'City',
      required: true,
    },
    {
      name: 'address',
      className: styles.dataInputs,
      type: 'text',
      placeholder: 'Address',
      required: true,
    },
    {
      name: 'phone',
      className: styles.dataInputs,
      type: 'tel',
      placeholder: 'Phone Number',
      required: true,
    },
  ];

  const handleRegisterLinkClick = (e) => {
    e.preventDefault();
    setShowRegisterForm(true);
  };

  const footerMessage = !showRegisterForm ? (
    <>
      Already have an account? <a href="/">Sign In</a>
    </>
  ) : (
    <>
      Don't have an account?{' '}
      <a href="/" onClick={handleRegisterLinkClick}>
        Click to Register
      </a>
    </>
  );

  return (
    <main>
      {showRegisterForm ? (
        <Form
          fields={registerFields}
          handleSubmit={handleRegisterSubmit}
          footer={footerMessage}
        />
      ) : (
        <Form
          fields={loginFields}
          handleSubmit={handleLoginSubmit}
          footer={footerMessage}
        />
      )}

      {err && <p className={styles.err}>{err}</p>}

      {!showRegisterForm ? (
        <p className={styles.register}>
          Don't have an account?{' '}
          <a href="/" onClick={handleRegisterLinkClick}>
            Click to Register
          </a>
        </p>
      ) : (
        <p className={styles.register}>
          Already have an account? <a href="/">Sign In</a>
        </p>
      )}
    </main>
  );
}

export default LoginRegister;
