import React, { useContext, useState } from 'react';
import { AuthContext } from '../../context/authContext';
import { validateForm } from './ValidateForm';
import Button from '../../components/button/Button';
import styles from './LoginRegister.module.css';

function LoginRegister() {
  const [loginInputs, setLoginInputs] = useState({
    username: '',
    password: '',
  });

  const [registerInputs, setRegisterInputs] = useState({
    username: '',
    email: '',
    password: '',
    city: '',
    address: '',
    phone: '',
    confirm_password: '',
  });

  const [loginErr, setLoginError] = useState(null);
  const [registerErr, setRegisterError] = useState(null);
  const [showRegisterForm, setShowRegisterForm] = useState(false);
  const { login, registered } = useContext(AuthContext);

  const handleLoginChange = (e) => {
    setLoginInputs((prev) => ({ ...prev, [e.target.name]: e.target.value }));
  };

  const handleRegisterChange = (e) => {
    setRegisterInputs((prev) => ({ ...prev, [e.target.name]: e.target.value }));
  };

  const handleLoginSubmit = async (e) => {
    e.preventDefault();
    try {
      await login(loginInputs);
    } catch (err) {
      setLoginError(err.response.data);
    }
  };

  const handleRegisterSubmit = async (e) => {
    e.preventDefault();
    setRegisterError(null); // Reset the error state
    const isValid = validateForm(registerInputs, setRegisterError);
    if (isValid) {
      try {
        await registered(registerInputs);
        // Handle registration completion logic if needed
      } catch (err) {
        setRegisterError(err.response.data);
      }
    }
  };

  const handleRegisterLinkClick = (e) => {
    e.preventDefault();
    setShowRegisterForm(true);
  };

  return (
    <main>
      {showRegisterForm ? (
        <form className={styles.form}>
          <h1>Register Form</h1>
          <input
            onChange={handleRegisterChange}
            className={styles.dataInputs}
            type="text"
            id="username"
            name="username"
            placeholder="User Name"
            required
          />
          <input
            onChange={handleRegisterChange}
            className={styles.dataInputs}
            type="email"
            id="email"
            name="email"
            placeholder="Email"
            required
          />
          <input
            onChange={handleRegisterChange}
            className={styles.dataInputs}
            type="password"
            id="password"
            name="password"
            placeholder="Password"
            required
          />
          <input
            onChange={handleRegisterChange}
            className={styles.dataInputs}
            type="password"
            id="confirm_password"
            name="confirm_password"
            placeholder="Confirm Password"
            required
          />
          <input
            onChange={handleRegisterChange}
            className={styles.dataInputs}
            type="text"
            id="city"
            name="city"
            placeholder="City"
            required
          />
          <input
            onChange={handleRegisterChange}
            className={styles.dataInputs}
            type="text"
            id="address"
            name="address"
            placeholder="Address"
            required
          />
          <input
            onChange={handleRegisterChange}
            className={styles.dataInputs}
            type="tel"
            id="phone"
            name="phone"
            placeholder="Phone Number"
            required
          />
          {registerErr && <p className={styles.err}>{registerErr}</p>}
          <Button name="Register" fun={handleRegisterSubmit} />
          <p className={styles.register}>
            Already have an account?{' '}
            <a href="/" onClick={() => setShowRegisterForm(false)}>
              Sign In
            </a>
          </p>
        </form>
      ) : (
        <form className={styles.form}>
          <h1 className={styles.head}>Welcome to Eco Collectors!</h1>
          <h2 className={styles.head}>Please log in to continue</h2>
          <input
            onChange={handleLoginChange}
            className={styles.dataInputs}
            type="text"
            id="username"
            name="username"
            placeholder="User Name"
            required
          />
          <input
            onChange={handleLoginChange}
            className={styles.dataInputs}
            type="password"
            id="password"
            name="password"
            placeholder="Password"
            required
          />
          {loginErr && <p className={styles.err}>{loginErr}</p>}
          <button onClick={handleLoginSubmit} type="submit">
            Login
          </button>
          <p className={styles.register}>
            Don't have an account?{' '}
            <a href="/" onClick={handleRegisterLinkClick}>
              Click to Register
            </a>
          </p>
        </form>
      )}
    </main>
  );
}

export default LoginRegister;
