import React, { useContext, useState } from 'react';
import { AuthContext } from '../../context/authContext';
import { validateForm } from './ValidateForm';
import RegisterForm from '../register/Register';
import styles from './AuthForm.module.css';

function AuthForm() {
  const [inputs, setInputs] = useState({
    username: '',
    email: '',
    password: '',
    city: '',
    address: '',
    phone: '',
    confirm_password: '',
  });

  const [err, setError] = useState(null);
  const { registered, login } = useContext(AuthContext);

  const [showRegisterForm, setShowRegisterForm] = useState(false);

  const handleChange = (e) => {
    setInputs((prev) => ({ ...prev, [e.target.name]: e.target.value }));
  };

  const handleRegister = async (e) => {
    e.preventDefault();
    setError(null); // Reset the error state
    const isValid = validateForm(inputs, setError);
    if (isValid) {
      try {
        await registered(inputs);
      } catch (err) {
        setError(err.response.data);
      }
    }
  };

  const handleLogin = async (e) => {
    e.preventDefault();
    setError(null); // Reset the error state
    try {
      await login(inputs);
    } catch (err) {
      setError(err.response.data);
    }
  };

  const handleRegisterLinkClick = (e) => {
    e.preventDefault();
    setShowRegisterForm(true);
  };

  return (
    <main>
      {showRegisterForm ? (
        <RegisterForm />
      ) : (
        <form className={styles.form}>
          <h1 className={styles.head}>Welcome to Eco Collectors!</h1>
          <h2 className={styles.head}>
            Please {err ? 'register' : 'log in'} to continue
          </h2>
          {!err && (
            <>
              <input
                onChange={handleChange}
                className={styles.dataInputs}
                type="text"
                id="username"
                name="username"
                placeholder="User Name"
                required
              />
              <input
                onChange={handleChange}
                className={styles.dataInputs}
                type="password"
                id="password"
                name="password"
                placeholder="Password"
                required
              />
            </>
          )}
          {err && <p className={styles.err}>{err}</p>}
          {!err ? (
            <button onClick={handleLogin} type="submit">
              Login
            </button>
          ) : (
            <>
              <input
                onChange={handleChange}
                className={styles.dataInputs}
                type="email"
                id="email"
                name="email"
                placeholder="Email"
                required
              />
              <input
                onChange={handleChange}
                className={styles.dataInputs}
                type="password"
                id="confirm_password"
                name="confirm_password"
                placeholder="Confirm Password"
                required
              />
              <input
                onChange={handleChange}
                className={styles.dataInputs}
                type="text"
                id="city"
                name="city"
                placeholder="City"
                required
              />
              <input
                onChange={handleChange}
                className={styles.dataInputs}
                type="text"
                id="address"
                name="address"
                placeholder="Address"
                required
              />
              <input
                onChange={handleChange}
                className={styles.dataInputs}
                type="tel"
                id="phone"
                name="phone"
                placeholder="Phone Number"
                required
              />
              <button onClick={handleRegister} type="submit">
                Register
              </button>
            </>
          )}
          <p className={styles.register}>
            {err ? 'Already have an account? ' : "Don't have an account? "}
            <a href="/" onClick={handleRegisterLinkClick}>
              {err ? 'Sign In' : 'Register'}
            </a>
          </p>
        </form>
      )}
    </main>
  );
}

export default AuthForm;
