import React, { useState, useContext } from 'react';
import Button from '../../components/button/Button';
import { AuthContext } from '../../context/authContext';
import axios from 'axios';
import styles from './Register.module.css';

function RegisterForm() {
  const [inputs, setInputs] = useState({
    username: '',
    email: '',
    password: '',
    city: '',
    address: '',
    phone: '',
  });

  // For displaying error for the user
  const [err, setError] = useState(null);

  const { registered } = useContext(AuthContext);

  // User Credentials
  const handleChange = (e) => {
    setInputs((prev) => ({ ...prev, [e.target.name]: e.target.value }));
  };

  // Submitting the register form
  async function handleSubmit(e) {
    e.preventDefault();
    try {
      await registered(inputs);
    } catch (err) {
      setError(err.response.data);
    }
  }

  return (
    <main>
      <form className={styles.form}>
        <h1>Register Form</h1>
        <input
          onChange={handleChange}
          className={styles.dataInputs}
          type="text"
          id="username"
          name="username"
          placeholder="User Name"
          required
        />
        <input
          onChange={handleChange}
          className={styles.dataInputs}
          type="email"
          id="email"
          name="email"
          placeholder="Email"
          required
        />
        <input
          onChange={handleChange}
          className={styles.dataInputs}
          type="password"
          id="password"
          name="password"
          placeholder="Password"
          required
        />
        <input
          className={styles.dataInputs}
          type="password"
          id="confirm_password"
          name="confirm_password"
          placeholder="Confirm Password"
          required
        />
        <input
          onChange={handleChange}
          className={styles.dataInputs}
          type="text"
          id="city"
          name="city"
          placeholder="City"
          required
        />
        <input
          onChange={handleChange}
          className={styles.dataInputs}
          type="text"
          id="address"
          name="address"
          placeholder="Address"
          required
        />
        <input
          onChange={handleChange}
          className={styles.dataInputs}
          type="tel"
          id="phone"
          name="phone"
          placeholder="Phone Number"
          required
        />
        {err && <p className={styles.err}>{err}</p>}
        <Button name="Click" fun={handleSubmit} />
        <p className={styles.register}>
          Already have an account? <a href="/">Sign In</a>
        </p>
      </form>
    </main>
  );
}

//<Button name="Register" onClick={handleSubmit} type="submit" />

export default RegisterForm;
