import React, { useState, useContext } from 'react';
import Button from '../../components/button/Button';
import { AuthContext } from '../../context/authContext';
import { validateForm } from './ValidateForm';
import styles from './AuthForm.module.css';

function AuthForm() {
  const [isRegistering, setIsRegistering] = useState(false);
  const [inputs, setInputs] = useState({
    username: '',
    email: '',
    password: '',
    city: '',
    address: '',
    phone: '',
    confirm_password: '',
  });
  const [err, setError] = useState(null);
  const { registered, login } = useContext(AuthContext);

  const handleChange = (e) => {
    setInputs((prev) => ({ ...prev, [e.target.name]: e.target.value }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError(null); // Reset the error state
    const isValid = validateForm(inputs, setError);
    if (isValid) {
      try {
        if (isRegistering) {
          await registered(inputs);
        } else {
          await login(inputs);
        }
      } catch (err) {
        setError(err.response.data);
      }
    }
  };

  const toggleRegistering = () => {
    setIsRegistering((prev) => !prev);
    setError(null); // Reset the error state
    setInputs({
      username: '',
      email: '',
      password: '',
      city: '',
      address: '',
      phone: '',
      confirm_password: '',
    });
  };

  return (
    <main>
      <form className={styles.form}>
        <h1 className={styles.head}>Welcome to Eco Collectors!</h1>
        <h2 className={styles.head}>
          {isRegistering ? 'Register' : 'Log In'} to continue
        </h2>
        {isRegistering && (
          <>
            <input
              onChange={handleChange}
              className={styles.dataInputs}
              type="text"
              id="username"
              name="username"
              placeholder="User Name"
              required
            />
            <input
              onChange={handleChange}
              className={styles.dataInputs}
              type="email"
              id="email"
              name="email"
              placeholder="Email"
              required
            />
            <input
              onChange={handleChange}
              className={styles.dataInputs}
              type="password"
              id="password"
              name="password"
              placeholder="Password"
              required
            />
            <input
              onChange={handleChange}
              className={styles.dataInputs}
              type="password"
              id="confirm_password"
              name="confirm_password"
              placeholder="Confirm Password"
              required
            />
            <input
              onChange={handleChange}
              className={styles.dataInputs}
              type="text"
              id="city"
              name="city"
              placeholder="City"
              required
            />
            <input
              onChange={handleChange}
              className={styles.dataInputs}
              type="text"
              id="address"
              name="address"
              placeholder="Address"
              required
            />
            <input
              onChange={handleChange}
              className={styles.dataInputs}
              type="tel"
              id="phone"
              name="phone"
              placeholder="Phone Number"
              required
            />
          </>
        )}
        {!isRegistering && (
          <>
            <input
              onChange={handleChange}
              className={styles.dataInputs}
              type="text"
              id="username"
              name="username"
              placeholder="User Name"
              required
            />
            <input
              onChange={handleChange}
              className={styles.dataInputs}
              type="password"
              id="password"
              name="password"
              placeholder="Password"
              required
            />
          </>
        )}
        {err && <p className={styles.err}>{err}</p>}
        <Button
          name={isRegistering ? 'Register' : 'Log In'}
          fun={handleSubmit}
        />
        <p className={styles.register}>
          {isRegistering
            ? 'Already have an account?'
            : "Don't have an account?"}
          <button
            className={styles.toggleButton}
            onClick={toggleRegistering}
            type="button"
          >
            {isRegistering ? 'Log In' : 'Register'}
          </button>
        </p>
      </form>
    </main>
  );
}

export default AuthForm;
