import React, { useState, useEffect } from 'react';
import {
  fetchBins,
  addBin,
  deleteBin,
  formatDate,
} from '../../utils/BinsUtils';
import BinForm from '../../components/binForm/BinForm';
import Button from '../../components/button/Button';
import classes from './RecycleBinsLocations.module.css';

function RecycleBinsLocations() {
  const [bins, setBins] = useState([]);

  // Fetching all bins
  useEffect(() => {
    const fetchData = async () => {
      const data = await fetchBins();
      setBins(data);
    };

    fetchData();
  }, [bins]);

  // Deleting a bin
  const handleDelete = async (id) => {
    const confirmation = window.confirm(
      `Are you sure you want to delete bin: ${id}?`
    );

    if (confirmation) {
      try {
        await deleteBin(id);
        setBins((prevBins) => prevBins.filter((bin) => bin.id !== id));
        //console.log('Bin deleted successfully!');
      } catch (error) {
        console.error('Error deleting bin:', error);
      }
    }
  };

  const handleAddBin = async (binData) => {
    try {
      // Perform an API call to add the new bin to the server
      // After successful addition, update the bins state with the new bin
      const response = await addBin(binData);
      const newBin = response.data;
      setBins((prevBins) => [...prevBins, newBin]);
      console.log('Bin added successfully!');
    } catch (error) {
      console.error('Error adding bin:', error);
    }
  };

  return (
    <main className={classes.container}>
      <h1>Bins Locations</h1>
      {bins.length === 0 ? (
        <p>No bins found.</p>
      ) : (
        <table>
          <thead>
            <tr>
              <th>ID</th>
              <th>Address</th>
              <th>City</th>
              <th>Type</th>
              <th>Last Modified</th>
              <th>Actions</th>
            </tr>
          </thead>
          <tbody>
            {bins.map((bin) => (
              <tr key={bin.id}>
                <td>{bin.id}</td>
                <td>{bin.address}</td>
                <td>{bin.city}</td>
                <td>{bin.type}</td>
                <td>{formatDate(bin.last_modified)}</td>
                <td>
                  <div className={classes.buttonMargin}>
                    <Button name="Delete" fun={() => handleDelete(bin.id)}>
                      Delete
                    </Button>
                    <Button name="Update">Delete</Button>
                  </div>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      )}
      <BinForm onSubmit={handleAddBin} />
    </main>
  );
}

export default RecycleBinsLocations;
