import React, { useContext, useState } from 'react';
import { AuthContext } from './context/authContext';
import Header from './components/header/Header';
import Footer from './components/footer/Footer';
import RegisterForm from './pages/register/Register';
import Login from './pages/login/Login';
import Home from './pages/home/Home';
import RecycleBinsLocations from './pages/RecycleBinsLocations/RecycleBinsLocations';
import Recyclers from './pages/Recyclers/Recyclers';

const App = () => {
  const { currentUser, registrationComplete } = useContext(AuthContext);
  const [selectedPage, setSelectedPage] = useState('home');

  const handleNavigation = (page) => {
    setSelectedPage(page);
  };

  const renderPage = () => {
    if (currentUser) {
      switch (selectedPage) {
        case 'home':
          return <Home />;
        case 'bins':
          return <RecycleBinsLocations />;
        case 'recyclers':
          return <Recyclers />;
        default:
          return null;
      }
    } else {
      return !registrationComplete ? <Login /> : <RegisterForm />;
    }
  };

  return (
    <>
      {currentUser ? (
        <>
          <Header onNavigation={handleNavigation} />
          <Home />
        </>
      ) : (
        <>{!registrationComplete ? <Login /> : <RegisterForm />}</>
      )}
      <Footer />
    </>
  );
};

export default App;
