import React, { useState } from 'react';
import './style.css';

const SignInForm = () => {
  return (
    <div className="form sign-in">
      <h2>Welcome back,</h2>
      <label>
        <span>Email</span>
        <input type="email" />
      </label>
      <label>
        <span>Password</span>
        <input type="password" />
      </label>
      <p className="forgot-pass">Forgot password?</p>
      <button type="button" className="submit">
        Sign In
      </button>
    </div>
  );
};

const SignUpForm = () => {
  return (
    <div className="form sign-up">
      <h2>Time to feel like home,</h2>
      <label>
        <span>Name</span>
        <input type="text" />
      </label>
      <label>
        <span>Email</span>
        <input type="email" />
      </label>
      <label>
        <span>Password</span>
        <input type="password" />
      </label>
      <button type="button" className="submit">
        Sign Up
      </button>
    </div>
  );
};

const LoginRegisterForm = () => {
  const [isSignup, setIsSignup] = useState(false);

  const handleButtonClick = () => {
    setIsSignup((prevState) => !prevState);
  };

  return (
    <div>
      <div className={`cont ${isSignup ? 's--signup' : ''}`}>
        {isSignup ? <SignUpForm /> : <SignInForm />}
        <div className="sub-cont">
          <div className="img">
            <div className={`img__text ${isSignup ? 'm--up' : 'm--in'}`}>
              <h2>{isSignup ? 'New here?' : 'One of us?'}</h2>
              <p>
                {isSignup
                  ? 'Sign up and discover great opportunities!'
                  : "If you already have an account, just sign in. We've missed you!"}
              </p>
            </div>
            <div className="img__btn" onClick={handleButtonClick}>
              <span className={`m--${isSignup ? 'up' : 'in'}`}>
                {isSignup ? 'Sign Up' : 'Sign In'}
              </span>
            </div>
          </div>
          {isSignup ? null : <SignUpForm />}
        </div>
      </div>
    </div>
  );
};

export default LoginRegisterForm;
