// pages/LoginRegister/LoginRegister.js

import React, { useContext, useState } from 'react';
import { AuthContext } from '../../context/authContext';
import Form from '../../components/Form'; // Import the Form component
import styles from './LoginRegister.module.css';

function LoginRegister() {
  const { login, registered } = useContext(AuthContext);

  const [inputs, setInputs] = useState({
    username: '',
    email: '',
    password: '',
    confirm_password: '',
    city: '',
    address: '',
    phone: '',
  });

  const [err, setError] = useState(null);
  const [showRegisterForm, setShowRegisterForm] = useState(false);

  const handleChange = (e) => {
    setInputs((prev) => ({ ...prev, [e.target.name]: e.target.value }));
  };

  const handleLoginSubmit = async (formData) => {
    try {
      await login(formData);
    } catch (err) {
      setError(err.response.data);
    }
  };

  const handleRegisterSubmit = async (formData) => {
    setError(null);
    try {
      await registered(formData);
      // Handle registration completion logic if needed
    } catch (err) {
      setError(err.response.data);
    }
  };

  // Fields for the login form
  const loginFields = [
    {
      name: 'username',
      className: styles.dataInputs,
      type: 'text',
      placeholder: 'User Name',
      required: true,
    },
    {
      name: 'password',
      className: styles.dataInputs,
      type: 'password',
      placeholder: 'Password',
      required: true,
    },
  ];

  // Fields for the registration form
  const registerFields = [
    {
      name: 'username',
      className: styles.dataInputs,
      type: 'text',
      placeholder: 'User Name',
      required: true,
    },
    {
      name: 'email',
      className: styles.dataInputs,
      type: 'email',
      placeholder: 'Email',
      required: true,
    },
    {
      name: 'password',
      className: styles.dataInputs,
      type: 'password',
      placeholder: 'Password',
      required: true,
    },
    {
      name: 'confirm_password',
      className: styles.dataInputs,
      type: 'password',
      placeholder: 'Confirm Password',
      required: true,
    },
    {
      name: 'city',
      className: styles.dataInputs,
      type: 'text',
      placeholder: 'City',
      required: true,
    },
    {
      name: 'address',
      className: styles.dataInputs,
      type: 'text',
      placeholder: 'Address',
      required: true,
    },
    {
      name: 'phone',
      className: styles.dataInputs,
      type: 'tel',
      placeholder: 'Phone Number',
      required: true,
    },
  ];

  const handleRegisterLinkClick = (e) => {
    e.preventDefault();
    setShowRegisterForm((prev) => !prev);
  };

  const footerMessage = showRegisterForm ? (
    <>
      Already have an account?{' '}
      <a href="/" onClick={handleRegisterLinkClick}>
        Click to Sign In
      </a>
    </>
  ) : (
    <>
      Don't have an account?{' '}
      <a href="/" onClick={handleRegisterLinkClick}>
        Click to Register
      </a>
    </>
  );

  return (
    <main>
      <Form
        fields={showRegisterForm ? registerFields : loginFields}
        handleSubmit={
          showRegisterForm ? handleRegisterSubmit : handleLoginSubmit
        }
        footer={footerMessage}
      />

      {err && <p className={styles.err}>{err}</p>}
    </main>
  );
}

export default LoginRegister;
