import React, { useState, useEffect } from 'react';
import { fetchBins, deleteBin } from '../../utils/BinsUtils';
import Button from '../../components/button/Button';
import classes from './RecycleBinsLocations.module.css';

function RecycleBinsLocations() {
  const [bins, setBins] = useState([]);

  // Fetching user request by user id
  useEffect(() => {
    const fetchData = async () => {
      const data = await fetchBins();
      setBins(data);
    };

    fetchData();
  }, []);

  const handleDelete = async (id) => {
    try {
      await deleteBin(id);
      setBins((prevBins) => prevBins.filter((bin) => bin.id !== id));
      console.log('Bin deleted successfully!');
    } catch (error) {
      console.error('Error deleting bin:', error);
    }
  };

  const formatDate = (dateString) => {
    const date = new Date(dateString);
    return date.toLocaleDateString('en-GB');
  };

  return (
    <main className={classes.container}>
      <h1>Bins Locations</h1>
      {bins.length === 0 ? (
        <p>No bins found.</p>
      ) : (
        <table>
          <thead>
            <tr>
              <th>ID</th>
              <th>Address</th>
              <th>City</th>
              <th>Type</th>
              <th>Last Modified</th>
              <th>Actions</th>
            </tr>
          </thead>
          <tbody>
            {bins.map((bin) => (
              <tr key={bin.id}>
                <td>{bin.id}</td>
                <td>{bin.address}</td>
                <td>{bin.city}</td>
                <td>{bin.type}</td>
                <td>{formatDate(bin.last_modified)}</td>
                <td>
                  <Button name="Delete" onClick={() => handleDelete(bin.id)}>
                    Delete
                  </Button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      )}
    </main>
  );
}

export default RecycleBinsLocations;
