import React, { useState, useEffect } from 'react';
import {
  fetchBins,
  addBin,
  deleteBin,
  formatDate,
} from '../../utils/BinsUtils';
import Button from '../../components/button/Button';
import classes from './RecycleBinsLocations.module.css';

function RecycleBinsLocations() {
  const [bins, setBins] = useState([]);
  const [address, setAddress] = useState('');
  const [city, setCity] = useState('');
  const [type, setType] = useState('');

  // Fetching all bins
  useEffect(() => {
    const fetchData = async () => {
      const data = await fetchBins();
      setBins(data);
    };

    fetchData();
  }, [bins]);

  // Deleting a bin
  const handleDelete = async (id) => {
    const confirmation = window.confirm(
      `Are you sure you want to delete bin: ${id}?`
    );

    if (confirmation) {
      try {
        await deleteBin(id);
        setBins((prevBins) => prevBins.filter((bin) => bin.id !== id));
        //console.log('Bin deleted successfully!');
      } catch (error) {
        console.error('Error deleting bin:', error);
      }
    }
  };

  // Add a bin
  const handleAddBin = async (e) => {
    e.preventDefault();
    const currentDate = new Date().toISOString(); // Get the current date
    const newBin = {
      address,
      city,
      type,
      last_modified: currentDate,
    };

    try {
      const data = await addBin(newBin);
      setBins((prevBins) => [...prevBins, data]);
      setAddress('');
      setCity('');
      setType('');
      console.log('Bin added successfully!');
    } catch (error) {
      console.error('Error adding bin:', error);
    }
  };

  return (
    <main className={classes.container}>
      <h1>Bins Locations</h1>
      <form className={classes.addBin} onSubmit={handleAddBin}>
        <input
          type="text"
          placeholder="Address"
          value={address}
          onChange={(e) => setAddress(e.target.value)}
          required
        />
        <input
          type="text"
          placeholder="City"
          value={city}
          onChange={(e) => setCity(e.target.value)}
          required
        />
        <input
          type="text"
          placeholder="Type"
          value={type}
          onChange={(e) => setType(e.target.value)}
          required
        />
        <button type="submit">Add Bin</button>
      </form>
      {bins.length === 0 ? (
        <p>No bins found.</p>
      ) : (
        <table>
          <thead>
            <tr>
              <th>ID</th>
              <th>Address</th>
              <th>City</th>
              <th>Type</th>
              <th>Last Modified</th>
              <th>Actions</th>
            </tr>
          </thead>
          <tbody>
            {bins.map((bin) => (
              <tr key={bin.id}>
                <td>{bin.id}</td>
                <td>{bin.address}</td>
                <td>{bin.city}</td>
                <td>{bin.type}</td>
                <td>{formatDate(bin.last_modified)}</td>
                <td>
                  <div className={classes.buttonMargin}>
                    <Button name="Delete" fun={() => handleDelete(bin.id)}>
                      Delete
                    </Button>
                    <Button name="Update">Delete</Button>
                  </div>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      )}
    </main>
  );
}

export default RecycleBinsLocations;
