import React, { useContext, useState } from 'react';
import { AuthContext } from '../../context/authContext';

import styles from './Login.module.css';

function LoginForm() {
  const [inputs, setInputs] = useState({
    username: '',
    password: '',
  });

  const [err, setError] = useState(null);

  // For displaying the current user name on screen - localStorage
  const { login } = useContext(AuthContext);

  const handleChange = (e) => {
    setInputs((prev) => ({ ...prev, [e.target.name]: e.target.value }));
  };

  // Submitting the register from authContext
  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      // Calling the login function
      await login(inputs);
    } catch (err) {
      setError(err.response.data);
    }
  };

  return (
    <main>
      <form>
        <h1 className={styles.head}>Welcome to Eco Collectors!</h1>
        <h2 className={styles.form}>Please log in to continue</h2>
        <input
          onChange={handleChange}
          className={styles.dataInputs}
          type="text"
          id="username"
          name="username"
          placeholder="User Name"
          required
        />
        <input
          onChange={handleChange}
          className={styles.dataInputs}
          type="password"
          id="password"
          name="password"
          placeholder="Password"
          required
        />
        {err && <p className={styles.err}>{err}</p>}
        <button onClick={handleSubmit} type="submit">
          Login
        </button>
        <p>
          Don't have an account? <a href="/register">Click here to register</a>
        </p>
      </form>
    </main>
  );
}

export default LoginForm;
