import axios from 'axios';

export async function fetchBins() {
  //console.log(id);
  try {
    const res = await axios.get(`/bins`);
    console.log(res.data);
    return res.data;
  } catch (err) {
    console.log(err);
    return null;
  }
}

export const deleteBin = async (id) => {
  try {
    const response = await fetch(`/api/bins/${id}`, {
      method: 'DELETE',
      headers: {
        'Content-Type': 'application/json',
      },
    });

    if (!response.ok) {
      throw new Error('Failed to delete bin');
    }

    const data = await response.json();
    return data;
  } catch (error) {
    throw new Error('Failed to delete bin: ' + error.message);
  }
};

export const formatDate = (dateString) => {
  const date = new Date(dateString);
  return date.toLocaleDateString('en-GB');
};
