// pages/LoginRegister/LoginRegister.js

import React, { useContext, useState } from 'react';
import { AuthContext } from '../../context/authContext';
import Form from '../../components/Form'; // Import the Form component
import styles from './LoginRegister.module.css';

function LoginRegister() {
  const { login, registered } = useContext(AuthContext);

  const [inputs, setInputs] = useState({
    username: '',
    password: '',
  });

  const [err, setError] = useState(null);
  const [showRegisterForm, setShowRegisterForm] = useState(false);

  const handleChange = (e) => {
    setInputs((prev) => ({ ...prev, [e.target.name]: e.target.value }));
  };

  const handleLoginSubmit = async (formData) => {
    try {
      await login(formData);
    } catch (err) {
      setError(err.response.data);
    }
  };

  const handleRegisterSubmit = async (formData) => {
    setError(null);
    try {
      await registered(formData);
      // Handle registration completion logic if needed
    } catch (err) {
      setError(err.response.data);
    }
  };

  // Fields for the login form
  const loginFields = [
    {
      name: 'username',
      className: styles.dataInputs,
      type: 'text',
      placeholder: 'User Name',
      required: true,
    },
    {
      name: 'password',
      className: styles.dataInputs,
      type: 'password',
      placeholder: 'Password',
      required: true,
    },
  ];

  // Fields for the registration form
  const registerFields = [
    {
      name: 'username',
      className: styles.dataInputs,
      type: 'text',
      placeholder: 'User Name',
      required: true,
    },
    {
      name: 'email',
      className: styles.dataInputs,
      type: 'email',
      placeholder: 'Email',
      required: true,
    },
    // Add more fields for registration as needed
  ];

  const handleRegisterLinkClick = (e) => {
    e.preventDefault();
    setShowRegisterForm(true);
  };

  return (
    <main>
      {showRegisterForm ? (
        <Form fields={registerFields} handleSubmit={handleRegisterSubmit} />
      ) : (
        <Form fields={loginFields} handleSubmit={handleLoginSubmit} />
      )}

      {err && <p className={styles.err}>{err}</p>}

      {!showRegisterForm ? (
        <p className={styles.register}>
          Don't have an account?{' '}
          <a href="/" onClick={handleRegisterLinkClick}>
            Click to Register
          </a>
        </p>
      ) : (
        <p className={styles.register}>
          Already have an account? <a href="/">Sign In</a>
        </p>
      )}
    </main>
  );
}

export default LoginRegister;
