import React, { useState, useEffect } from 'react';
import { fetchBins, deleteBin } from '../../utils/BinsUtils';
import Button from '../../components/button/Button';
import classes from './RecycleBinsLocations.module.css';

function RecycleBinsLocations() {
  const [bins, setBins] = useState([]);
  const [sortColumn, setSortColumn] = useState('');
  const [sortDirection, setSortDirection] = useState('');

  // Fetching user request by user id
  useEffect(() => {
    const fetchData = async () => {
      const data = await fetchBins();
      setBins(data);
    };

    fetchData();
  }, []);

  const handleDelete = async (id) => {
    try {
      await deleteBin(id);
      setBins((prevBins) => prevBins.filter((bin) => bin.id !== id));
      console.log('Bin deleted successfully!');
    } catch (error) {
      console.error('Error deleting bin:', error);
    }
  };

  const handleSort = (column) => {
    if (sortColumn === column) {
      setSortDirection(sortDirection === 'asc' ? 'desc' : 'asc');
    } else {
      setSortColumn(column);
      setSortDirection('asc');
    }
  };

  const sortBins = () => {
    const sortedBins = [...bins];

    sortedBins.sort((a, b) => {
      const aValue = a[sortColumn];
      const bValue = b[sortColumn];

      if (aValue < bValue) return sortDirection === 'asc' ? -1 : 1;
      if (aValue > bValue) return sortDirection === 'asc' ? 1 : -1;
      return 0;
    });

    return sortedBins;
  };

  const sortedBins = sortBins();

  return (
    <main className={classes.container}>
      <h1>Bins Locations</h1>
      {bins.length === 0 ? (
        <p>No bins found.</p>
      ) : (
        <table>
          <thead>
            <tr>
              <th>
                <a
                  className={
                    sortColumn === 'address'
                      ? `filter__link ${sortDirection}`
                      : 'filter__link'
                  }
                  onClick={() => handleSort('address')}
                  href="#"
                >
                  Address
                </a>
              </th>
              <th>
                <a
                  className={
                    sortColumn === 'city'
                      ? `filter__link ${sortDirection}`
                      : 'filter__link'
                  }
                  onClick={() => handleSort('city')}
                  href="#"
                >
                  City
                </a>
              </th>
              <th>
                <a
                  className={
                    sortColumn === 'type'
                      ? `filter__link ${sortDirection}`
                      : 'filter__link'
                  }
                  onClick={() => handleSort('type')}
                  href="#"
                >
                  Type
                </a>
              </th>
              <th>
                <a
                  className={
                    sortColumn === 'last_modified'
                      ? `filter__link ${sortDirection}`
                      : 'filter__link'
                  }
                  onClick={() => handleSort('last_modified')}
                >
                  Last Modified
                </a>
              </th>
              <th>Delete</th>
            </tr>
          </thead>
          <tbody>
            {sortedBins.map((bin) => (
              <tr key={bin.id}>
                <td>{bin.address}</td>
                <td>{bin.city}</td>
                <td>{bin.type}</td>
                <td>{bin.last_modified}</td>
                <td>
                  <Button name="Delete" onClick={() => handleDelete(bin.id)}>
                    Delete
                  </Button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      )}
    </main>
  );
}

export default RecycleBinsLocations;
